con:config.script:
// 游늷 Pedir la ruta del archivo CSV al usuario
def csvFilePath = System.console()?.readLine("Ingrese la ruta del archivo CSV: ")

// 游늷 Validar si el usuario ingres칩 la ruta
if (!csvFilePath || csvFilePath.trim().isEmpty()) {
    throw new IllegalArgumentException("丘멆잺 ERROR: No ingres칩 ninguna ruta.")
}

// 游늷 Verificar si el archivo existe
def file = new File(csvFilePath)
if (!file.exists()) {
    throw new FileNotFoundException("丘멆잺 ERROR: No se encontr칩 el archivo CSV en la ruta: ${csvFilePath}")
}

// 游늷 Leer el contenido del CSV
def lines = file.readLines()
if (lines.size() < 2) {
    throw new IllegalArgumentException("丘멆잺 ERROR: El archivo CSV est치 vac칤o o no tiene suficientes filas.")
}

// 游늷 Obtener las columnas
def headers = lines[0].split(";")
def values = lines[1].split(";")
def dataMap = [:]

headers.eachWithIndex { header, index ->
    dataMap[header.trim()] = values[index]?.trim()
}

// 游늷 Asignar valores a propiedades en SoapUI
context.testCase.setPropertyValue("idCliente", dataMap["idCliente"])
context.testCase.setPropertyValue("usuario", dataMap["usuario"])
context.testCase.setPropertyValue("fecha", dataMap["fecha"])
context.testCase.setPropertyValue("endpoint", dataMap["endpoint"])
context.testCase.setPropertyValue("statusEsperado", dataMap["statusEsperado"])

// 游늷 Mostrar datos cargados
log.info("游늷 Datos cargados: idCliente='${dataMap["idCliente"]}', usuario='${dataMap["usuario"]}', fecha='${dataMap["fecha"]}'")

